mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT p.post_id , p.post_writer_id , p.acc_person , p.acc_start_date , p.acc_end_date , p.nickname , p.board_name , p.post_grade , p.post_title , p.post_date , count(c.cmt_id) comments , p.views , p.blocked , p.deleted , p.post_like_count FROM post p, comments c, tag t
p.post_title LIKE '%${search.searchKeyword}%'
p.post_writer_id LIKE '%${search.searchKeyword}%'
t.tag_content LIKE '%${search.searchKeyword}%'
AND p.post_id = c.post_id(+) AND p.post_id = t.post_id(+) AND p.board_name = #{boardName}
p.post_id = c.post_id(+) AND p.post_id = t.post_id(+) AND p.board_name = #{boardName}
GROUP BY p.post_id , p.post_writer_id , p.acc_person , p.acc_start_date , p.acc_end_date , p.nickname , p.board_name , p.post_grade , p.post_title , p.post_date , p.views , p.blocked , p.deleted , p.post_like_count ORDER BY p.post_date desc ) inner_table WHERE ROWNUM <= #{search.currentPage}*#{search.pageSize} ) WHERE row_seq BETWEEN (#{search.currentPage}-1)*#{search.pageSize}+1 AND #{search.currentPage}*#{search.pageSize}

SELECT 
user_id 
FROM USERS 
WHERE user_name = #{userName}  AND  role != 'x' 

	AND email = #{email}


	AND phone = #{phone}

mapper.update:
UPDATE 
USERS SET pwd = #{pwd} 
WHERE user_id = #{userId}

UPDATE
USERS SET  nickname = #{nickname} ,  email = #{email} , phone = #{phone} , user_img = #{userImg} , sex = #{sex} , birth = #{birth}  , push_agree = #{pushAgree}
WHERE user_id = #{userId}

UPDATE
USERS SET role = 'Q'
WHERE user_id = #{value}

UPDATE
USERS SET role = 'X' AND nickname =  null
WHERE user_id = #{value}

mapper.select:
SELECT  *
from(
	SELECT inner_table.*, ROWNUM AS row_seq
	from(
		SELECT
		user_id , point_id , ref_id , used_type , used_point , used_date
		from Point
		WHERE user_id = #{userId}
		
			AND used_type = 'U' OR used_type = 'S'
		
		
			AND used_type = 'C' OR used_type = 'F' OR used_type = 'R'
		
		order By used_date DESC) inner_table
	WHERE rownum<= #{search.currentPage}*#{search.pageSize} ) 
WHERE row_seq BETWEEN (#{search.currentPage}-1)*#{search.pageSize}+1 AND #{search.currentPage}*#{search.pageSize}

SELECT COUNT(*) from Point WHERE user_id = #{userId}
AND used_type = 'U' OR used_type = 'S'
AND used_type = 'C' OR used_type = 'F' OR used_type = 'R'

SELECT  *
from(
	SELECT inner_table.*, ROWNUM AS row_seq
	from(
		SELECT
		 c.post_id , c.cmt_id , c.nickname , c.writer_id , c.cmt_date , c.cmt_content , c.secret , c.deleted , c.blocked , c.cmt_like_count
		from Comments c
		WHERE c.writer_id= #{userId}
		order By c.cmt_date DESC) inner_table
	WHERE rownum<= #{search.currentPage}*#{search.pageSize} ) 
WHERE row_seq BETWEEN (#{search.currentPage}-1)*#{search.pageSize}+1 AND #{search.currentPage}*#{search.pageSize}
