mapper.update:
 	UPDATE post

 		post_title   = #{postTitle},
 		post_content = #{postContent}

	WHERE post_id = #{postId}

 	UPDATE post

 		post_like_count = post_like_count + 1

	WHERE post_id = #{postId}

 	UPDATE post

 		views = views + 1

	WHERE post_id = #{postId}

UPDATE post
day_best = day_best + 1, week_best = week_best + 1, month_best = month_best + 1
WHERE post_id = #{postId}

mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT p.post_id , p.post_writer_id , p.board_name , p.post_grade , p.post_title , p.post_content , p.post_date , count(c.parent_cmt_id) comments , p.views , p.blocked , p.deleted , p.post_like_count FROM post p, comments c, tag t
p.post_title LIKE '%${search.searchKeyword}%'
p.post_writer_id LIKE '%${search.searchKeyword}%'
t.tag_content LIKE '%${search.searchKeyword}%'
AND p.post_id = c.post_id(+) AND p.post_id = t.post_id(+) AND p.board_name = #{boardName}
p.post_id = c.post_id(+) AND p.post_id = t.post_id(+) AND p.board_name = #{boardName}
GROUP BY p.post_id , p.post_writer_id , p.board_name , p.post_grade , p.post_title , p.post_content , p.post_date , p.views , p.blocked , p.deleted , p.post_like_count ORDER BY p.post_date desc ) inner_table WHERE ROWNUM <= #{search.currentPage}*#{search.pageSize} ) WHERE row_seq BETWEEN (#{search.currentPage}-1)*#{search.pageSize}+1 AND #{search.currentPage}*#{search.pageSize}

mapper.update:
UPDATE comments

		cmt_content = #{cmtContent:VARCHAR},
		secret      = #{secret:CHAR}

WHERE cmt_id = #{cmtId}
