mapper.update:
UPDATE likes

	like_check = 'T'

WHERE like_user_id = #{likeUserId} AND ref_id = #{refId}

UPDATE likes

	like_check = 'F'

WHERE like_user_id = #{likeUserId} AND ref_id = #{refId}

mapper.select:
SELECT COUNT(like_id)
FROM likes
WHERE like_user_id = #{likeUserId} AND ref_id = #{refId}
